import React, { useState } from "react";
import "./BudgetLimits.css";

function BudgetLimits({ selected }) {
  const highSpending = Object.entries(selected)
    .filter(([_, level]) => level >= 3);

  const [limits, setLimits] = useState({});

  const handleChange = (cat, value) => {
    setLimits({ ...limits, [cat]: value });
  };

  const handleSave = () => {
    console.log("Spending Levels:", selected);
    console.log("Monthly Limits:", limits);
    alert("Budget preferences saved!");
  };

  return (
    <div className="budget-container">
      <h2>Set monthly spending limit</h2>
      <p>We will alert you when you reach this limit</p>

      {highSpending.map(([cat]) => (
        <div key={cat} className="limit-block">
          <label>{cat}</label>
          <input
            type="number"
            placeholder="Enter monthly limit â‚¹"
            onChange={(e) => handleChange(cat, e.target.value)}
          />
        </div>
      ))}

      <button className="save-btn" onClick={handleSave}>
        Save Preferences
      </button>
    </div>
  );
}

export default BudgetLimits;
