// src/components/login.js
import React, { useState } from "react";
import "./Login.css";

function Login({ onLogin }) {
  const [isLogin, setIsLogin] = useState(true);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");

  const toggleForm = () => {
    setIsLogin(!isLogin);
    setMessage("");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const url = isLogin ? "http://localhost:5000/login" : "http://localhost:5000/register";
    const bodyData = isLogin
      ? { email, password }
      : { name, email, password };

    try {
      const res = await fetch(url, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(bodyData),
      });

      const data = await res.json();

      if (!res.ok) {
        setMessage(data.message || "Server error");
        return;
      }

      setMessage(data.message);

      if (isLogin && data.user) {
        // pass user info to AppRouter global state
        onLogin(data.user);
      }

      if (!isLogin) {
        // after registration, optionally auto-login
        onLogin(data.user || null);
      }
    } catch (err) {
      console.error(err);
      setMessage("Server connection failed");
    }
  };

  return (
    <div className="main-container">
      <div className="left-panel">
        <h1>Expense Intelligence</h1>
        <p>Smart Financial Management for Modern Users</p>
      </div>

      <div className="right-panel">
        <div className="form-box">
          <h2 id="form-title">{isLogin ? "Login" : "Register"}</h2>

          <form onSubmit={handleSubmit}>
            {!isLogin && (
              <input
                type="text"
                placeholder="Full Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            )}
            <input
              type="email"
              placeholder="Email Address"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <button type="submit">{isLogin ? "Login" : "Register"}</button>
          </form>

          <p className="toggle-text">
            <span>{isLogin ? "Don't have an account?" : "Already have an account?"}</span>
            <span id="toggle-btn" onClick={toggleForm}>
              {isLogin ? "Register" : "Login"}
            </span>
          </p>

          {message && <p id="message">{message}</p>}
        </div>
      </div>
    </div>
  );
}

export default Login;
