import React from "react";

const categories = [
  { name: "Fixed Income", icon: "üíº", desc: "Same income every month" },
  { name: "Variable Income", icon: "üìä", desc: "Income changes monthly" },
  { name: "Freelancer / Gig", icon: "üßë‚Äçüíª", desc: "Project-based earnings" },
  { name: "Business Owner", icon: "üè¢", desc: "Runs a business" },
  { name: "No Income", icon: "üéì", desc: "Student or not earning" },
  { name: "Multiple Sources", icon: "üí∞", desc: "More than one income type" }
];

export default function IncomeCategory({ onSelect }) {
  return (
    <div className="section-container">
      <div className="section-title">Tell us about your income</div>
      <div className="section-subtitle">
        This helps us personalize your financial plan
      </div>

      <div className="card-grid">
        {categories.map(item => (
          <div
            key={item.name}
            className="glass-card"
            onClick={() => onSelect(item.name)}
          >
            <div className="card-icon">{item.icon}</div>
            <div className="card-title">{item.name}</div>
            <div className="card-subtitle">{item.desc}</div>
          </div>
        ))}
      </div>
    </div>
  );
}
