import React, { useState, useEffect } from "react";
import "./BudgetLimits.css";

function BudgetLimits({ formData }) {
  const [limits, setLimits] = useState({});
  const categories = Object.keys(formData.spendingPriority || {}); // auto get selected categories

  const handleChange = (category, value) => {
    setLimits({ ...limits, [category]: Number(value) });
  };

  const handleSubmit = async () => {
    const finalData = { ...formData, budgetLimits: limits };

    try {
      const response = await fetch("http://localhost:5000/api/userdata/save", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(finalData),
      });

      const data = await response.json();
      console.log(data);
      alert("Your data has been saved successfully!");
    } catch (error) {
      console.error(error);
      alert("Error saving data");
    }
  };

  return (
    <div className="spending-container">
      <h1 className="spending-title">Set Monthly Limits for Major Expenses</h1>

      <div className="spending-grid">
        {categories.length === 0 && <p>No spending categories selected.</p>}
        {categories.map((category) => (
          <div key={category} className="spending-card active">
            <h3>{category}</h3>
            <input
              type="number"
              placeholder="Enter monthly amount"
              className="limit-input"
              onChange={(e) => handleChange(category, e.target.value)}
            />
          </div>
        ))}
      </div>

      <button className="spending-button" onClick={handleSubmit}>
        Submit
      </button>
    </div>
  );
}

export default BudgetLimits;
