import React from "react";
import "./IncomeCategory.css";

const categories = [
  { name: "Fixed Income", icon: "üíº", desc: "Same income every month" },
  { name: "Variable Income", icon: "üìä", desc: "Income changes monthly" },
  { name: "Freelancer / Gig", icon: "üßë‚Äçüíª", desc: "Project based earnings" },
  { name: "Business Owner", icon: "üè¢", desc: "Runs a business" },
  { name: "No Income", icon: "üéì", desc: "Student or not earning" },
  { name: "Multiple Sources", icon: "üí∞", desc: "More than one income" },
];

function IncomeCategory({ onNext }) {
  return (
    <div className="income-container">
      <h1 className="income-title">Tell us about your income</h1>
      <p className="income-subtitle">
        This helps us personalize your financial plan
      </p>

      <div className="income-grid">
        {categories.map((item) => (
          <div
            key={item.name}
            className="income-card"
            onClick={onNext}
          >
            <div className="income-icon">{item.icon}</div>
            <div className="income-name">{item.name}</div>
            <div className="income-desc">{item.desc}</div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default IncomeCategory;
