import React, { useState } from "react";
import "./IncomeCategory.css";

const categories = [
  { id: "fixed", title: "Fixed Income", desc: "Same income every month", icon: "ðŸ’¼" },
  { id: "variable", title: "Variable Income", desc: "Income changes monthly", icon: "ðŸ“Š" },
  { id: "freelancer", title: "Freelancer / Gig", desc: "Project-based earnings", icon: "ðŸ§‘â€ðŸ’»" },
  { id: "business", title: "Business Owner", desc: "Runs a business", icon: "ðŸ¢" },
  { id: "none", title: "No Income", desc: "Student or not earning", icon: "ðŸŽ“" },
  { id: "multiple", title: "Multiple Sources", desc: "More than one income type", icon: "ðŸ’°" }
];

function IncomeCategory({ onSelect }) {
  const [selected, setSelected] = useState(null);

  const handleSelect = (id) => {
    setSelected(id);
    if (onSelect) onSelect(id);
  };

  return (
    <div className="income-container">
      <h2 className="title">Tell us about your income</h2>
      <p className="subtitle">This helps us personalize your financial plan</p>

      <div className="card-grid">
        {categories.map((cat) => (
          <div
            key={cat.id}
            className={`card ${selected === cat.id ? "selected" : ""}`}
            onClick={() => handleSelect(cat.id)}
          >
            <div className="card-icon">{cat.icon}</div>
            <h3>{cat.title}</h3>
            <p>{cat.desc}</p>
          </div>
        ))}
      </div>

      {selected && (
        <button className="continue-btn">
          Continue â†’
        </button>
      )}
    </div>
  );
}

export default IncomeCategory;
