import React, { useState } from "react";
import "./SpendingPriority.css";

const categories = [
  { name: "Food & Groceries", icon: "ðŸ”" },
  { name: "Transport", icon: "ðŸš—" },
  { name: "Shopping", icon: "ðŸ›ï¸" },
  { name: "Entertainment", icon: "ðŸŽ¬" },
  { name: "Bills", icon: "ðŸ“„" },
  { name: "Health", icon: "ðŸ’Š" },
];

function SpendingPriority({ onNext }) {
  const [selected, setSelected] = useState([]);

  const toggleCategory = (name) => {
    setSelected((prev) =>
      prev.includes(name)
        ? prev.filter((item) => item !== name)
        : [...prev, name]
    );
  };

  return (
    <div className="spending-container">
      <h1 className="spending-title">Where do you spend the most?</h1>
      <p className="spending-subtitle">Select all that apply</p>

      <div className="spending-grid">
        {categories.map((item) => (
          <div
            key={item.name}
            className={`spending-card ${
              selected.includes(item.name) ? "active" : ""
            }`}
            onClick={() => toggleCategory(item.name)}
          >
            <div className="spending-icon">{item.icon}</div>
            <div className="spending-name">{item.name}</div>
          </div>
        ))}
      </div>

      <button
        className="spending-button"
        onClick={() => onNext(selected)}
      >
        Continue
      </button>
    </div>
  );
}

export default SpendingPriority;
