import React, { useState } from "react";
import IncomeCategory from "./components/IncomeCategory";
import Questionnaire from "./components/Questionnaire";
import SpendingPriority from "./components/spendPriority";

function App() {
  const [step, setStep] = useState(1);
  const [incomeType, setIncomeType] = useState(null);

  // Step 1 → user selects income category
  const handleIncomeSelect = (type) => {
    setIncomeType(type);
    setStep(2);
  };

  // Step 2 → questionnaire completed
  const handleQuestionnaireComplete = () => {
    setStep(3);
  };

  return (
    <div>
      {step === 1 && (
        <IncomeCategory onSelect={handleIncomeSelect} />
      )}

      {step === 2 && (
        <Questionnaire
          incomeType={incomeType}
          onComplete={handleQuestionnaireComplete}
        />
      )}

      {step === 3 && (
        <spendPriority />
      )}
    </div>
  );
}

export default App;
