import React, { useState } from "react";
import "./App.css";

import IncomeCategory from "./components/IncomeCategory";
import Questionnaire from "./components/Questionnaire";
import SpendingPriority from "./components/SpendingPriority";
import BudgetLimits from "./components/BudgetLimits";

function App() {
  const [step, setStep] = useState(1);
  const [selectedCategories, setSelectedCategories] = useState([]);

  return (
    <div className="App">
      {step === 1 && <IncomeCategory onNext={() => setStep(2)} />}

      {step === 2 && <Questionnaire onNext={() => setStep(3)} />}

      {step === 3 && (
        <SpendingPriority
          onNext={(data) => {
            setSelectedCategories(data);
            setStep(4);
          }}
        />
      )}

      {step === 4 && (
        <BudgetLimits categories={selectedCategories} />
      )}
    </div>
  );
}

export default App;
