import React, { useState } from "react";
import "./App.css";

import IncomeCategory from "./components/IncomeCategory";
import Questionnaire from "./components/Questionnaire";
import SpendingPriority from "./components/SpendingPriority";
import BudgetLimits from "./components/BudgetLimits";

function App() {
  const [step, setStep] = useState(1);
  const [spendingLevels, setSpendingLevels] = useState({});
  const [highSpending, setHighSpending] = useState([]);

  return (
    <div className="App">
      {step === 1 && <IncomeCategory onNext={() => setStep(2)} />}

      {step === 2 && <Questionnaire onNext={() => setStep(3)} />}

      {step === 3 && (
        <SpendingPriority
          onNext={(levels) => {
            setSpendingLevels(levels);

            const filtered = Object.entries(levels)
              .filter(([_, value]) => value >= 3)
              .map(([key]) => key);

            setHighSpending(filtered);
            console.log("Spending intensity:", levels);
            setStep(4);
          }}
        />
      )}

      {step === 4 && (
        <BudgetLimits categories={highSpending} />
      )}
    </div>
  );
}

export default App;
