import React, { useState } from "react";
import BudgetLimits from "./BudgetLimits";
import "./spendPriority.css";

const categories = [
  "Food & Groceries",
  "Transport",
  "Shopping",
  "Entertainment",
  "Bills",
  "Health"
];

function SpendingPriority() {
  const [selected, setSelected] = useState({});
  const [showBudget, setShowBudget] = useState(false);

  const toggleCategory = (cat) => {
    setSelected({
      ...selected,
      [cat]: selected[cat] ?? 0
    });
  };

  const updateLevel = (cat, value) => {
    setSelected({
      ...selected,
      [cat]: Number(value)
    });
  };

  if (showBudget) {
    return <BudgetLimits selected={selected} />;
  }

  return (
    <div className="spending-container">
      <h2>Select where you spend the most</h2>
      <p>You can select multiple categories</p>

      <div className="card-grid">
        {categories.map((cat) => (
          <div
            key={cat}
            className={`card ${selected[cat] !== undefined ? "active" : ""}`}
            onClick={() => toggleCategory(cat)}
          >
            {cat}
          </div>
        ))}
      </div>

      {Object.keys(selected).length > 0 && (
        <div className="slider-section">
          <h3>Adjust spending level (0–5)</h3>

          {Object.keys(selected).map((cat) => (
            <div key={cat} className="slider-block">
              <label>{cat}: {selected[cat]}</label>
              <input
                type="range"
                min="0"
                max="5"
                value={selected[cat]}
                onChange={(e) => updateLevel(cat, e.target.value)}
              />
            </div>
          ))}

          <button
            className="next-btn"
            onClick={() => setShowBudget(true)}
          >
            Continue →
          </button>
        </div>
      )}
    </div>
  );
}

export default SpendingPriority;
