import React, { useState } from "react";
import Login from "./components/Login";
import IncomeCategory from "./components/IncomeCategory";
import Questionnaire from "./components/Questionnaire";
import SpendingPriority from "./components/SpendingPriority";
import BudgetLimits from "./components/BudgetLimits";
import "./App.css";

function App() {
  const [step, setStep] = useState(1);

  // Centralized form data
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    incomeType: "",
    category: "",
    questionnaire: {},
    spendingPriority: {},
    budgetLimits: {},
  });

  const nextStep = () => setStep(step + 1);

  const updateFormData = (section, data) => {
    setFormData((prev) => ({ ...prev, [section]: data }));
  };

  return (
    <div className="app-container">
      {/* Step 1: Login */}
      {step === 1 && (
        <Login
          onNext={(userData) => {
            // Optional: capture name/email from registration
            updateFormData("name", userData?.name || "");
            updateFormData("email", userData?.email || "");
            nextStep();
          }}
        />
      )}

      {/* Step 2: IncomeCategory */}
      {step === 2 && (
        <IncomeCategory
          onNext={(data) => {
            updateFormData("incomeType", data.incomeType);
            updateFormData("category", data.category);
            nextStep();
          }}
        />
      )}

      {/* Step 3: Questionnaire */}
      {step === 3 && (
        <Questionnaire
          onNext={(data) => {
            updateFormData("questionnaire", data);
            nextStep();
          }}
        />
      )}

      {/* Step 4: SpendingPriority */}
      {step === 4 && (
        <SpendingPriority
          onNext={(data) => {
            updateFormData("spendingPriority", data);
            nextStep();
          }}
        />
      )}

      {/* Step 5: BudgetLimits */}
      {step === 5 && <BudgetLimits formData={formData} />}
    </div>
  );
}

export default App;
