import React, { useState } from "react";
import IncomeCategory from "./components/IncomeCategory";
import Questionnaire from "./components/Questionnaire";
import SpendingPriority from "./components/SpendingPriority";
import BudgetLimits from "./components/BudgetLimits";

function App() {
  const [step, setStep] = useState(1);

  // Centralized form data
  const [formData, setFormData] = useState({
    incomeType: "",
    category: "",
    questionnaire: {},
    spendingPriority: {},
    budgetLimits: {},
  });

  // Move to next step
  const nextStep = () => setStep(step + 1);

  // Update data from child components
  const updateFormData = (section, data) => {
    setFormData((prev) => ({ ...prev, [section]: data }));
  };

  return (
    <div className="App">
      {step === 1 && (
        <IncomeCategory
          onNext={(data) => {
            // IncomeCategory sends incomeType + category
            updateFormData("incomeType", data.incomeType);
            updateFormData("category", data.category);
            nextStep();
          }}
        />
      )}
      {step === 2 && (
        <Questionnaire
          onNext={(data) => {
            // Questionnaire sends all answers
            updateFormData("questionnaire", data);
            nextStep();
          }}
        />
      )}
      {step === 3 && (
        <SpendingPriority
          onNext={(data) => {
            // SpendingPriority sends levels of selected categories
            updateFormData("spendingPriority", data);
            nextStep();
          }}
        />
      )}
      {step === 4 && <BudgetLimits formData={formData} />}
    </div>
  );
}

export default App;
