import React, { useState } from "react";

const categories = [
  "Food & Groceries",
  "Transport",
  "Shopping",
  "Entertainment",
  "Bills",
  "Health"
];

export default function SpendingPriority({ onNext }) {
  const [selected, setSelected] = useState([]);

  const toggle = cat => {
    setSelected(prev =>
      prev.includes(cat)
        ? prev.filter(c => c !== cat)
        : [...prev, cat]
    );
  };

  return (
    <div className="section-container">
      <div className="section-title">Where do you spend most?</div>
      <div className="section-subtitle">
        Select categories where spending is high
      </div>

      <div className="card-grid">
        {categories.map(cat => (
          <div
            key={cat}
            className={`glass-card ${selected.includes(cat) ? "active" : ""}`}
            onClick={() => toggle(cat)}
          >
            <div className="card-title">{cat}</div>
          </div>
        ))}
      </div>

      <button onClick={() => onNext(selected)}>Continue</button>
    </div>
  );
}
