import React, { useState } from "react";
import "./IncomeCategory.css";

const categories = [
  { id: "fixed", title: "Fixed Income", desc: "Same income every month" },
  { id: "variable", title: "Variable Income", desc: "Income changes monthly" },
  { id: "freelancer", title: "Freelancer / Gig", desc: "Project-based earnings" },
  { id: "business", title: "Business Owner", desc: "Runs a business" },
  { id: "none", title: "No Income", desc: "Student or not earning" },
  { id: "multiple", title: "Multiple Sources", desc: "More than one income type" }
];

function IncomeCategory({ onSelect }) {
  const [selected, setSelected] = useState(null);

  const handleSelect = (id) => {
    setSelected(id);
    if (onSelect) onSelect(id);
  };

  return (
    <div className="income-container">
      <h2 className="title">Select Your Income Type</h2>

      <div className="card-grid">
        {categories.map((cat) => (
          <div
            key={cat.id}
            className={`card ${selected === cat.id ? "selected" : ""}`}
            onClick={() => handleSelect(cat.id)}
          >
            <h3>{cat.title}</h3>
            <p>{cat.desc}</p>
          </div>
        ))}
      </div>

      {selected && <button className="continue-btn">Continue</button>}
    </div>
  );
}

export default IncomeCategory;
