import React, { useState } from "react";
import "./Questionnaire.css";

function Questionnaire({ incomeType, onComplete }) {
  const [answers, setAnswers] = useState({});

  const handleChange = (question, value) => {
    setAnswers({ ...answers, [question]: value });
  };

  // Questions based on income type
  const getIncomeQuestions = () => {
    if (incomeType === "fixed") {
      return [
        {
          key: "incomeRange",
          label: "Monthly Income Range",
          options: [
            "Less than ₹20,000",
            "₹20,000 – ₹50,000",
            "₹50,000 – ₹1,00,000",
            "Above ₹1,00,000"
          ]
        },
        {
          key: "salaryFrequency",
          label: "Salary Payment Frequency",
          options: ["Monthly", "Bi-weekly", "Weekly"]
        }
      ];
    }

    if (
      incomeType === "variable" ||
      incomeType === "freelancer" ||
      incomeType === "business"
    ) {
      return [
        {
          key: "incomeRange",
          label: "Average Monthly Income Range",
          options: [
            "Less than ₹20,000",
            "₹20,000 – ₹50,000",
            "₹50,000 – ₹1,00,000",
            "Above ₹1,00,000"
          ]
        },
        {
          key: "incomeStability",
          label: "Income Stability",
          options: [
            "Very predictable",
            "Somewhat variable",
            "Highly unpredictable"
          ]
        }
      ];
    }

    return [
      {
        key: "moneySource",
        label: "Primary Money Source",
        options: [
          "Parents / Family",
          "Scholarship",
          "Part-time work",
          "Other"
        ]
      }
    ];
  };

  // Common financial insight questions
  const commonQuestions = [
    {
      key: "financialGoal",
      label: "Your main financial goal right now",
      options: [
        "Save more money",
        "Control spending",
        "Pay off debt",
        "Invest for future",
        "Emergency fund"
      ]
    },
    {
      key: "savingHabit",
      label: "Do you save money regularly?",
      options: ["Yes", "No"]
    },
    {
      key: "mainExpense",
      label: "Where does most of your money go?",
      options: [
        "Housing / Rent",
        "Food & Groceries",
        "Transport",
        "Education",
        "Lifestyle / Shopping",
        "Family Responsibilities",
        "Not sure"
      ]
    },
    {
      key: "spendingLevel",
      label: "How would you describe your monthly spending?",
      options: [
        "Spend less than I earn",
        "Spend about what I earn",
        "Spend more than I earn"
      ]
    }
  ];

  const questions = [...getIncomeQuestions(), ...commonQuestions];

  const handleSubmit = () => {
    console.log("Income Type:", incomeType);
    console.log("User Answers:", answers);

    // Move to Spending Priority step
    if (onComplete) onComplete();
  };

  return (
    <div className="question-container">
      <h2>Tell us more about your finances</h2>

      {questions.map((q) => (
        <div key={q.key} className="question-block">
          <label>{q.label}</label>
          <select
            value={answers[q.key] || ""}
            onChange={(e) => handleChange(q.key, e.target.value)}
          >
            <option value="">Select an option</option>
            {q.options.map((opt) => (
              <option key={opt}>{opt}</option>
            ))}
          </select>
        </div>
      ))}

      <button className="submit-btn" onClick={handleSubmit}>
        Continue →
      </button>
    </div>
  );
}

export default Questionnaire;
