import React, { useState } from "react";
import IncomeCategory from "./components/IncomeCategory";

function App() {
  const [incomeType, setIncomeType] = useState(null);

  const handleIncomeSelect = (type) => {
    setIncomeType(type);
    console.log("Selected income type:", type);
  };

  return (
    <div>
      <IncomeCategory onSelect={handleIncomeSelect} />

      {/* Temporary debug display — we’ll replace this with next screen */}
      {incomeType && (
        <p style={{ textAlign: "center", marginTop: "20px", color: "#fff" }}>
          Selected: {incomeType}
        </p>
      )}
    </div>
  );
}

export default App;
